import React, { Component } from 'react';
import './App.css';
import {Dropdown, DropdownButton} from 'react-bootstrap'



class WeeklyLeaderboard extends Component {
  state = {
    leaderBoardData: []
  };

  componentDidMount() {
    this.fetchData()
        .then(res => this.setState({
          leaderBoardData: res
        }))
        //.then(res => console.log(res))
        .catch(err => console.log(err));
  }

  fetchData = async () => {
    const response = await fetch('/api/sudokleQueries/getWeeklyLeaderboardInfo');
    const body = response.json();
    return body;
  };

  fetchData2 = async () => {
    const response = await fetch('/api/sudokleQueries/getWeeklyLeaderboardInfo2');
    const body = response.json();
    return body;
  };

  handleSort = async () =>{
    console.log("hi");
    this.fetchData2()
        .then(res => this.setState(
            {
              leaderBoardData:res
            }
        ))
        .catch(err => console.log(err));
}
  handleSort2 = async() =>{
    console.log("hi2");
    this.fetchData()
        .then(res => this.setState(
            {
              leaderBoardData:res
            }
        ))
        .catch(err => console.log(err));
  }

  getRows(){
    const rows = [];
    var count = 1;
    for(const user of this.state.leaderBoardData){
      rows.push(<tr key={user.userID}><td>Rank: {count}</td><td>{user.userEmail}</td><td>{user.puzzlesSolved}</td><td>{user.averageTimeSolvedWeek}</td></tr>)
      count++;
    }
    return rows;
  }

  render() {
    const rows = this.getRows();

    return (
        <div className="App">
          <header className="App-header">
            <h1>Weekly Average Leaderboard</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Rank</th>
                      <th>Email</th>
                      <th>Puzzles Solved</th>
                      <th>Weekly Average Time Solve</th>
                      <th>
                        <DropdownButton id="dropdown-item-button" title="Sort By:">
                          <Dropdown.Item as="button" onClick={this.handleSort}>Puzzles Solved</Dropdown.Item>
                          <Dropdown.Item as="button" onClick={this.handleSort2}>Average Solve Time</Dropdown.Item>
                        </DropdownButton>
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {rows}
                  </tbody>
                </table>
          </header>
        </div>
    );
  }
}

export default WeeklyLeaderboard;
