import {BrowserRouter, Route, Routes} from "react-router-dom";
import Layout from "./Layout";
import Home from "./Home";
import WeeklyLeaderboard from "./WeeklyLeaderboard";
import React, {Component} from "react";
import DailyLeaderBoard from "./DailyLeaderBoard";



class App extends Component {
    constructor(props) {
        super(props);
        this.state = {value: '', value2: '', value3: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
        this.handleChange3 = this.handleChange3.bind(this);
    }



    handleChange(event) {
        this.setState({value: event.target.value});
    }
    handleChange2(event) {
        this.setState({value2: event.target.value2});
    }
    handleChange3(event) {
        this.setState({value3: event.target.value3});
    }

    createUser = async(event) => {
        event.preventDefault();
        try{
            let res = await fetch('/api/sudokleQueries/addUser', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    userEmail: this.state.value
                }),
            });
        }
        catch (e) {
            console.log(e);
        }
    };
   // " 7  2  46 6    89 2  8  715 84 97   71     59   13 48 697  2  8 58    6 43  8  7 "

    createGrid = async(event) => {
        event.preventDefault();
        try{
            let res = await fetch('/api/sudokleQueries/addSudoku', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    puzzle: this.state.value2,
                    solution: this.state.value3
                }),
            });
        }
        catch (e) {
            console.log(e);
        }
    };


    render() {

        return (
            <div>
                <BrowserRouter>
                    <Routes>
                        <Route path="/" element={<Layout/>}>
                            <Route index element={<Home/>} />
                            <Route path="DailyLeaderboard" element={<DailyLeaderBoard/>} />
                            <Route path="WeeklyLeaderboard" element={<WeeklyLeaderboard/>} />
                        </Route>
                    </Routes>
                </BrowserRouter>

                <form onSubmit={this.createUser}>
                    <label>
                        Email:
                        <input type="text" value={this.state.value} onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Add Test User" />
                </form>

                <form onSubmit={this.createGrid}>
                    <label>
                        Sudoku Grid:
                        <input type="text" value={this.state.value2} onChange={this.handleChange2} />
                        Sudoku Solution:
                        <input type="text" value={this.state.value3} onChange={this.handleChange3} />
                    </label>
                    <input type="submit" value="Add Sudokle Grid" />
                </form>

            </div>

        );
    }
}

export default App;
